mapper.insert:
INSERT INTO awsdb.coin_price
(
    coin_id
  , input_date
  , input_time
  , opening_price
  , high_price
  , low_price
  , prev_closing_price
  , trade_volume
  , acc_trade_price
  , acc_trade_price_24h
  , acc_trade_volume
  , acc_trade_volume_24h
)
values
(
    #{market}
  , date_format(now(), '%Y%m%d')
  , time_format(now(), '%H%i%s')
  , #{openingPrice}
  , #{highPrice}
  , #{lowPrice}
  , #{prevClosingPrice}
  , #{tradeVolume}
  , #{accTradePrice}
  , #{accTradePrice_24h}
  , #{accTradeVolume}
  , #{accTradeVolume_24h}
)

